import { useSelector } from "react-redux";
import { NavLink } from "react-router-dom";
import CartItem from "../components/CartItem"
import { useEffect, useState } from "react";
const Cart = () => {
  const { cart } = useSelector((state) => state);
  const [totalAmount, setAmount] = useState(0);

  useEffect(() => {
    setAmount(
      cart.reduce((acc, curr) => (acc += curr.price),0))}, [cart])
  return (
    <div className="flex flex-row justify-center items-center">
      {
        cart.length > 0 ?
          (<div className="flex flex-row w-10/12 justify-center items-center ">
            <div className="max-w-[600px]">
              {
                cart.map((item, index) => {
                  return <CartItem key={item.id} item={item} Cartindex={index} />
                })
              }
            </div>
            <div>
              <p>Your Cart</p>
              <h2>
                Summary
              </h2>
              <p>Total Item :{cart.length}</p>
            </div>
            <div>
              <p>Total Amount :{totalAmount}</p>
              <button>CheckOut</button>
            </div>

          </div>

          ) :
          (
            <div className="flex flex-col  gap-x-3 mx-auto relative top-[15rem]">
              <h2>No Item </h2>
              <NavLink to="/" >
                <button className="text-green-700 rounded-lg cursor-pointer border-2 px-6 py-2 border-green-700 hover:bg-green-600 hover:text-slate-100 ">Shop Now</button>
              </NavLink>
            </div>
          )
      }
    </div>
  );
};

export default Cart;
