import React from "react";
import { toast } from "react-hot-toast";
import { useDispatch, useSelector } from "react-redux";
import { add, remove } from "../redux/Slices/CartSlice"
const Product = ({ post }) => {
  const { cart } = useSelector((state) => state);
  const dispatch = useDispatch();
  const AddCart = () => {
    dispatch(add(post));
    toast.success("Item Added to Cart");
  }
  const RemoveCart = () => {
    dispatch(remove(post.id));
    toast.success("Item is Removed");
  }


  return (
    <div className="flex flex-col items-center justify-between hover:scale-110 transition duration-300 ease-in gap-3 p-4 mt-10 ml-5 rounded-xl outline shadow-[rgba(0,_0,_0,_0.4)_0px_30px_90px] " >
      <div>
        <p className="text-gray-700 font-semibold text-lg truncate w-40 mt-1">{post.title}</p>
      </div>
      <div>
        <p className="w-40 text-gray-400 font-normal text-[10px] text-left ">
          {post.description.split(" ").slice(0,10).join(" ") + "..."}
        </p>
      </div>
      <div className="h-[180px]">
        <img src={post.image} alt="Product" className="h-full w-full" />
      </div>
      <div className="flex">
        <div>

        <p> {post.price} </p>
      </div>
      <div>
        {
          cart.some((p) => p.id === post.id) ? (<button onClick={RemoveCart} >Remove Item</button>) : (<button onClick={AddCart} >Add To Cart</button>)
        }
      </div>
        </div>
    </div>
  );
};

export default Product;
