import React from "react";
import { useDispatch, useSelector } from "react-redux";
const Product = ({post}) => {
  const {cart} =useSelector((state)=>state);
  const dispatch = useDispatch();
  return (
<div className="" >
  <div>
 <p>  {post.title}</p>
    </div>
  <div>
    <p>
      
      {post.description}
      </p>
      </div>
  <div>
    <img src= {post.image} alt="Product" />
  </div>
  <div>
    <p> {post.price} </p>
  </div>
 <div>
  {
cart.some((p)=> p.id === post.id) ? (<button onClick={AddCart} >Remove Item</button>) : (<button onClick={RemoveCart} >Add To Cart</button>)
  }
 </div>
</div>
  );
};

export default Product;
