import React from "react";
import { useSelector } from "react-redux";
const Product = ({post}) => {
  const {cart} =useSelector((state)=>state);
  return (
<div className="" >
  <div>
 <p>  {post.title}</p>
    </div>
  <div>
    <p>
      
      {post.description}
      </p>
      </div>
  <div>
    <img src= {post.image} alt="Product" />
  </div>
  <div>
    <p> {post.price} </p>
  </div>
 <div>
  {
cart.some((p)=> p.id == post.id) ? (<p>Remove Item</p>) : (<p>Add To Cart</p>)
  }
 </div>
</div>
  );
};

export default Product;
